mapper.update:
update tb_team_user
set tu_invited = 'Y', tu_mid = #{ tuMID }
where tu_email = #{ tuEmail } and tu_tid = #{ tuTID }


update tb_team_user
set tu_leader = 'N'
where tu_tid = #{ tNo } and tu_mid = #{ tMID }


update tb_team_user
set tu_leader = 'Y'
where tu_tid = #{ tNo } and tu_mid = #{ tMID }

update tb_team_user
set tu_odate = sysdate
where tu_tid = #{ tNo } and tu_mid = #{ tMID }
